class EmgCollector(myo.DeviceListener):
  """
  Collects EMG data in a queue with *n* maximum number of elements.
  """
  def __init__(self, n):
    self.n = n
    self.lock = Lock()
    self.emg_data_queue = deque(maxlen=n)

  def get_emg_data(self):
    with self.lock:
      return list(self.emg_data_queue)

  # myo.DeviceListener

  def on_connected(self, event):
    event.device.stream_emg(True)
    event.device.request_battery_level()

  def on_emg(self, event):
    with self.lock:
      self.emg_data_queue.append(event.emg)
    # event.device.vibrate(myo.VibrationType.short)



class Plot(object):

  def __init__(self, listener):
    self.n = listener.n
    self.listener = listener
    self.fig = plt.figure()
    self.axes = [self.fig.add_subplot('81' + str(i)) for i in range(1, 9)]
    [(ax.set_ylim([-100, 100])) for ax in self.axes]
    self.graphs = [ax.plot(np.arange(self.n), np.zeros(self.n))[0] for ax in self.axes]
    plt.ion()

  def update_plot(self):
    emg_data = self.listener.get_emg_data()
    emg_data = np.array([x[1] for x in emg_data]).T
    for g, data in zip(self.graphs, emg_data):
      if len(data) < self.n:
        # Fill the left side with zeroes.
        data = np.concatenate([np.zeros(self.n - len(data)), data])
      g.set_ydata(data)
    plt.draw()

  def print_plot(self):
    emg_print = self.listener.get_emg_data()
    emg_print += [0] * (200 - len(emg_print))
    emg_print = np.array(emg_print)
    # print(emg_print.shape)
    # print(emg_print)
    emg_print = emg_print.reshape(1,200,8)
    a = model.predict(emg_print)

    def predict(x):
      return {
        0: 'null',
        1: 'down',
        2: 'left',
        3: 'rest',
        4: 'right',
        5: 'up',
      }[x]

    e = np.amax(a)
    r = np.where(a == e)
    print(predict(r[1][0]))

  def main(self):
    while True:
      self.print_plot()

def main():
  myo.init()
  hub = myo.Hub()
  listener = EmgCollector(200)
  with hub.run_in_background(listener.on_event):
    time.sleep(5)
    Plot(listener).print_plot()
    Plot(listener).main()
    EmgCollector(myo).vibrate_def()


if __name__ == '__main__':
  main()